{
    ItemResponse<ToDoActivity> createResponse = await containerWithTransportException.CreateItemAsync<ToDoActivity>(
      item: testItem,
      requestOptions: requestOptions);
    Assert.Fail("Should have thrown a Service Unavailable Exception");
}
